import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";

export const fetchWeather = createAsyncThunk(
  "weather/fetchWeather",
  async (city) => {
    const response = await fetch(`https://api.weatherapi.com/v1/current.json?key=fa1e04a97a1048989cb20516241501 &q=${city}&aqi=no`);

    const data = response.json();

    return data;
  }
)



const weatherSlice = createSlice({
  name: "weather",
  initialState: {
    weather: { "location": { "name": "London", "region": "City of London, Greater London", "country": "United Kingdom", "lat": 51.52, "lon": -0.11, "tz_id": "Europe/London", "localtime_epoch": 1705324458, "localtime": "2024-01-15 13:14" }, "current": { "last_updated_epoch": 1705323600, "last_updated": "2024-01-15 13:00", "temp_c": 3.0, "temp_f": 37.4, "is_day": 1, "condition": { "text": "Sunny", "icon": "//cdn.weatherapi.com/weather/64x64/day/113.png", "code": 1000 }, "wind_mph": 8.1, "wind_kph": 13.0, "wind_degree": 330, "wind_dir": "NNW", "pressure_mb": 1012.0, "pressure_in": 29.88, "precip_mm": 0.0, "precip_in": 0.0, "humidity": 56, "cloud": 0, "feelslike_c": -1.0, "feelslike_f": 30.2, "vis_km": 10.0, "vis_miles": 6.0, "uv": 2.0, "gust_mph": 13.2, "gust_kph": 21.3 } },
    status: null,
    error: null
  },
  reducers: {},
  extraReducers: (builder) => {
    builder.addCase(
      fetchWeather.fulfilled, (state, action) => {
        state.weather = action.payload;
      }
    )
  }
})

export default weatherSlice.reducer
