import React, { useEffect } from 'react'
import "./WeatherStyle/style.css"
import { useDispatch } from 'react-redux'
import { useSelector } from 'react-redux'
import { fetchWeather } from '../../store/weatherSlice'
import '../'

const Weather = () => {

  const weather = useSelector(state => state.weather.weather)

  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(fetchWeather("Kiew"))
  }, [])

  console.log(weather);

  return (
    <div className='weather'>
      <div className="weather-temp">
        <h2 className='weather-temp-title'>{weather.current.temp_f}</h2>
        <h2 className='weather-temp-postscript'>Today</h2>

        <div className="weather-text-container">
          <h3 className='weather-temp-text'>{weather.location.localtime}</h3>
          <h3 className='weather-temp-text'>Location: {weather.location.name}</h3>
        </div>

        <img src="." />

      </div>

      <div className="weather-main">
      </div>

    </div>
  )
}

export default Weather
